async function onButtonClick(code) {
if(code.startsWith('complete_')) {
const questId=code.replace('complete_','');
const quest=quests.find(q=>q.id===questId);

let currentPoints=parseInt(await getState("femPoints"));
currentPoints += Array.isArray(quest.points)? quest.points[0] : quest.points;

let stats=JSON.parse(await getState("stats"));

if(Array.isArray(quest.points)){
for(let i=1;i<quest.points.length;i++){
stats[quest.points[i]] += Math.floor(Math.random()*2)+1;
}
}

await setState("femPoints",currentPoints.toString());
await setState("stats",JSON.stringify(stats));

return `${getName()} gained ${Array.isArray(quest.points)? quest.points[0]:quest.points} feminization points!`;
}
}

